import React, { Component } from 'react'
import Card from './Card';

export default class Tours extends Component {
    constructor() {
        super()
        this.state = {
            cardone: ['3 day tours','Up to 30 people','2 tour guides','Sleep in cozy hotels','Difficulty: easy'],
            cardtwo: ['7 day tours','Up to 40 people','6 tour guides','Sleep in provided tents','Difficulty: medium'],
            cardthree: ['5 day tours','Up to 15 people','3 tour guides','Sleep in provided tents','Difficulty: hard'],
        }
    }
    render() {
        return(<section className='section-tours'>
            <div className='u-center-text u-margin-bottom-large'>
                <h2 className='hdng-sec'>Most popular tours</h2>
            </div>
            <div className='row'>
                <div className='col-1-of-3'><Card number='1' heading='The Sea Explorer' price='$297' list={this.state.cardone}/></div>
                <div className='col-1-of-3'><Card number='2' heading='The Forest Hiker' price='$497' list={this.state.cardtwo}/></div>
                <div className='col-1-of-3'><Card number='3' heading='The Snow Adventurer' price='$897' list={this.state.cardthree}/></div>
            </div>
            <div className='u-center-text u-margin-top-large'>
                <a href='#btn' className='btn btn--green'>Discover all tours</a>
            </div>
        </section>)
    }
}
